import {
  Await,
  defer,
  json,
  redirect,
  useRouteLoaderData,
} from "react-router-dom";
import EventItem from "../components/EventItem";
import EventsList from "../components/EventsList";
import { Suspense } from "react";

const EventDetailPage = () => {
  const { event, events } = useRouteLoaderData("event-detail");

  return (
    <>
    <Suspense>
      <Await resolve={event}>{(loadedEvent) => <EventItem event={event} />}</Await>

    </Suspense>
      <Await resolve={events}>{(loadedEvents) => <EventsList events={events} />}</Await>
    </>
  );
};

export default EventDetailPage;

async function loadEvent(id) {
  const response = await fetch("http://localhost:8080/events/" + id);

  if (!response.ok) {
    throw json(
      { message: "Could not fetch details for the selected event." },
      {
        status: 500,
      }
    );
  } else {
    const resData = response.json();
    return resData.event;
  }
}

async function loadEvents() {
  const response = await fetch("http://localhost:8080/events");

  if (!response.ok) {
    // return {isError: true, message: 'Could not fetch data.'}
    // throw new Response(
    //   JSON.stringify({ message: "Could not fetch data."}, {
    //     status: 500,
    //   })
    // );
    throw json({ message: "Could not fetch data." }, { status: 500 });
  } else {
    const resData = await response.json();
    return resData.events;
  }
}

export async function loader({ request, params }) {
  const id = params.eventid;

  return defer({
    event: loadEvent(id),
    events: loadEvents(),
  });
}

export async function action({ params, request }) {
  const eventid = params.eventid;

  const response = await fetch("http://localhost:8080/events/" + eventid, {
    method: request.method,
  });

  if (!response.ok) {
    throw json({ message: "Could not delete event" }, { status: 500 });
  }

  return redirect("/events");
}
